mapper.insert:
INSERT INTO
    TB_RECORD_GT4( SESSION_ID
             , PLAYER_ID
             , BEST_LAP
             , SECTOR1
             , SECTOR2
             , SECTOR3
             , CAR_MODEL
             , TRACK_SEQ
             , LAP_COUNT
             , REG_DT )
VALUES ( #{sessionId}
       , #{playerId}
       , #{bestLap}
       , #{sector1}
       , #{sector2}
       , #{sector3}
       , #{carModel}
       , #{trackSeq}
       , #{lapCount}
       , NOW() )

mapper.update:
UPDATE TB_RECORD SET
TRACK_SEQ = #{trackSeq}
, LAP_COUNT = LAP_COUNT + #{lapCount}

    ,BEST_LAP = #{bestLap}
    ,CAR_MODEL = #{carModel}


    ,SECTOR1 = #{sector1}


    ,SECTOR2 = #{sector2}


    ,SECTOR3 = #{sector3}

,UPD_DT = NOW()
WHERE PLAYER_ID = #{playerId}
AND SESSION_ID = #{sessionId}

UPDATE TB_RECORD_GT4 SET
TRACK_SEQ = #{trackSeq}
, LAP_COUNT = LAP_COUNT + #{lapCount}

    ,BEST_LAP = #{bestLap}
    ,CAR_MODEL = #{carModel}


    ,SECTOR1 = #{sector1}


    ,SECTOR2 = #{sector2}


    ,SECTOR3 = #{sector3}

,UPD_DT = NOW()
WHERE PLAYER_ID = #{playerId}
AND SESSION_ID = #{sessionId}

mapper.select:
SELECT ROUND(@ROWNUM := @ROWNUM +1) AS RANK , A.* FROM ( SELECT SESSION_ID, FIRST_NAME, LAST_NAME, TP.STEAM_AVATAR, TC.CAR_NAME, TC.CAR_MODEL, A.BEST_LAP, A.SECTOR1, A.SECTOR2, A.SECTOR3, A.LAP_COUNT, A.PLAYER_ID from TB_RECORD A INNER JOIN TB_TRACK TT on A.TRACK_SEQ = TT.SEQ INNER JOIN TB_PLAYER TP on A.PLAYER_ID = TP.PLAYER_ID INNER JOIN TB_CAR TC ON A.CAR_MODEL = TC.CAR_MODEL WHERE SESSION_ID = #{sessionId}
AND TEAM_23 = #{team23h}
ORDER BY BEST_LAP ) A , (SELECT @ROWNUM := 0) B

SELECT ROUND(@ROWNUM := @ROWNUM +1) AS RANK , A.* FROM ( SELECT SESSION_ID, FIRST_NAME, LAST_NAME, TP.STEAM_AVATAR, TC.CAR_NAME, TC.CAR_MODEL, A.BEST_LAP, A.SECTOR1, A.SECTOR2, A.SECTOR3, A.LAP_COUNT, A.PLAYER_ID from TB_RECORD_GT4 A INNER JOIN TB_TRACK TT on A.TRACK_SEQ = TT.SEQ INNER JOIN TB_PLAYER TP on A.PLAYER_ID = TP.PLAYER_ID INNER JOIN TB_CAR TC ON A.CAR_MODEL = TC.CAR_MODEL WHERE SESSION_ID = #{sessionId}
AND TEAM_23 = #{team23h}
ORDER BY BEST_LAP ) A , (SELECT @ROWNUM := 0) B

SELECT ROUND(@ROWNUM := @ROWNUM + 1) AS RANK, D.* FROM ( SELECT C.SESSION_ID, TP.FIRST_NAME, TP.LAST_NAME, TP.STEAM_AVATAR, TC.CAR_NAME, TC.CAR_MODEL, C.BEST_LAP, C.SECTOR1, C.SECTOR2, C.SECTOR3, C.LAP_COUNT, B.PLAYER_ID FROM (SELECT MIN(A.BEST_LAP) AS BEST_LAP, A.PLAYER_ID , MIN(A.SECTOR1) AS SECTOR1 FROM TB_RECORD A WHERE TRACK_SEQ = #{trackSeq} GROUP BY A.PLAYER_ID) B INNER JOIN TB_RECORD C ON B.PLAYER_ID = C.PLAYER_ID AND B.BEST_LAP = C.BEST_LAP AND B.SECTOR1 = C.SECTOR1 INNER JOIN TB_TRACK TT on C.TRACK_SEQ = TT.SEQ INNER JOIN TB_PLAYER TP on C.PLAYER_ID = TP.PLAYER_ID INNER JOIN TB_CAR TC ON C.CAR_MODEL = TC.CAR_MODEL
WHERE TEAM_23 = #{team23h}
ORDER BY BEST_LAP ) D, (SELECT @ROWNUM := 0) E

SELECT ROUND(@ROWNUM := @ROWNUM + 1) AS RANK, D.* FROM ( SELECT C.SESSION_ID, TP.FIRST_NAME, TP.LAST_NAME, TP.STEAM_AVATAR, TC.CAR_NAME, TC.CAR_MODEL, C.BEST_LAP, C.SECTOR1, C.SECTOR2, C.SECTOR3, C.LAP_COUNT, B.PLAYER_ID FROM (SELECT MIN(A.BEST_LAP) AS BEST_LAP, A.PLAYER_ID , MIN(A.SECTOR1) AS SECTOR1 FROM TB_RECORD_GT4 A WHERE TRACK_SEQ = #{trackSeq} GROUP BY A.PLAYER_ID) B INNER JOIN TB_RECORD_GT4 C ON B.PLAYER_ID = C.PLAYER_ID AND B.BEST_LAP = C.BEST_LAP AND B.SECTOR1 = C.SECTOR1 INNER JOIN TB_TRACK TT on C.TRACK_SEQ = TT.SEQ INNER JOIN TB_PLAYER TP on C.PLAYER_ID = TP.PLAYER_ID INNER JOIN TB_CAR TC ON C.CAR_MODEL = TC.CAR_MODEL
WHERE TEAM_23 = #{team23h}
ORDER BY BEST_LAP ) D, (SELECT @ROWNUM := 0) E
